var bttbtn = $('#bttbutton');

$(window).scroll(function() {
  if ($(window).scrollTop() > 300) {
    bttbtn.addClass('show');
  } else {
    bttbtn.removeClass('show');
  }
});

bttbtn.on('click', function(e) {
  e.preventDefault();
  $('html, body').animate({scrollTop:0}, '300');
});

